mapper.select:
select id, supply_id, floor_type, wood_type, floor_number,
       floor_spec, floor_rank,
       market_price,
       trade_price,
       technology, buckle_type, create_by, create_time, update_by, update_time, remark from floor_bid

     and supply_id = #{supplyId}
     and floor_type = #{floorType}
     and wood_type = #{woodType}
     and floor_number like CONCAT('%',#{floorNumber},'%')
     and floor_spec like CONCAT('%',#{floorSpec},'%')
     and floor_rank = #{floorRank}
     and price = #{price}
     and visible_price = #{visiblePrice}
     and market_price = #{marketPrice}
     and trade_price = #{tradePrice}
     and direct_price = #{directPrice}
     and cooperate_price = #{cooperatePrice}
     and technology = #{technology}
     and buckle_type = #{buckleType}

select id, supply_id, floor_type, wood_type, floor_number,
floor_spec, floor_rank,
market_price,
cooperate_price,
